import React, { useState, useEffect } from "react";
import { Nav } from "rsuite";
import { connect } from "react-redux";
import { fetchCodeSection } from "../../../actions/index";

const CodeList = ({ sections, user_data, ...props }) => {
  const [active, setActive] = useState();

  const handleSelect = (active) => {
    console.log(active)
    setActive(active);
  };

  const stateHandler = (obj) => {
    let index = sections.findIndex(obj)
    console.log(index)
  }

  return (
    <div>
      <Nav
        {...props}
        vertical
        activeKey={active}
        onSelect={handleSelect}
        appearance="subtle"
      >
        <Nav.Item> </Nav.Item>
        {sections.map((section) => (
          <Nav.Item 
            eventKey={section.code_id}
            onClick={()=>{
              stateHandler(section)
            }}>{section.code_title}</Nav.Item>
        ))}
      </Nav>
    </div>
  );
};

const mapStateToProps = (state) => {
  return {
    sections: state.projects_data.sections,
    user_data: state.user_data,
  };
};

export default connect(mapStateToProps, { fetchCodeSection })(CodeList);
