import React from "react";
import { Nav, Dropdown } from "rsuite";

const EditorMenu = ({snippets, setSnippets, selected}) => {
  const themes = [
    "monokai",
    "github",
    "tomorrow",
    "kuroir",
    "twilight",
    "xcode",
    "textmate",
    "solarized dark",
    "solarized light",
    "terminal",
  ];

  const modes = [
      "javascript",
      "java",
      "python",
      "xml",
      "ruby",
      "sass",
      "markdown",
      "mysql",
      "json",
      "html",
      "handlebars",
      "golang",
      "csharp",
      "coffee",
      "css"
  ];

  function snippetHandler(location) {
      setSnippets([
          ...snippets,
            {
                startRow: location[0].row,
                startCol: location[0].column,
                endRow: location[1].row,
                endCol: location[1].column,
                className: "markerTest2"
            }
      ])
      console.log(snippets)
  }

  return (
    <div
      style={{
        display: "flex",
        flexFlow: "row",
        justifyContent: "space-between",
        marginTop: "0.5rem",
        marginBottom: "0.5rem"
      }}
    >
      <Nav>
          <Nav.Item onClick={()=>{
              snippetHandler(selected)
          }}>+ Set Snippet</Nav.Item>
      </Nav>
      <Nav>
        <Dropdown title="Themes">
          {themes.map((theme) => (
            <Dropdown.Item>{theme}</Dropdown.Item>
          ))}
        </Dropdown>
        <Dropdown title="Editor Mode">
            {modes.map((mode)=>(
                <Dropdown.Item>{mode}</Dropdown.Item>
            ))}
        </Dropdown>
      </Nav>
    </div>
  );
};

export default EditorMenu;
