import React, { useState } from "react";
import { Nav, Dropdown, Modal, Divider, Tag, TagGroup } from "rsuite";
import { useForm } from "react-hook-form";
import InputColor from "react-input-color";
import NewSnippetForm from "./NewSnippetForm";

const EditorMenu = ({ snippets, setSnippets, selected, editorMode, theme }) => {
  const themes = [
    "monokai",
    "github",
    "tomorrow",
    "kuroir",
    "twilight",
    "xcode",
    "textmate",
    "solarized dark",
    "solarized light",
    "terminal",
  ];

  const modes = [
    "javascript",
    "java",
    "python",
    "xml",
    "ruby",
    "sass",
    "markdown",
    "mysql",
    "json",
    "html",
    "handlebars",
    "golang",
    "csharp",
    "coffee",
    "css",
  ];

  const [newSnippetMenu, setNewSnippetMenu] = useState({
    backdrop: false,
    show: false,
  });

  const open = () => {
    setNewSnippetMenu({
      backdrop: true,
      show: true,
    });
  };

  const close = () => {
    setNewSnippetMenu({
      backdrop: false,
      show: false,
    });
  };

