//               <Tag color="cyan">Tag One</Tag>
//               <Tag color="violet">Tag One</Tag>
//             </TagGroup>
//             <Divider style={{ width: "100%" }}><h2>or</h2></Divider>
//             <div>
//             <p>Pick a Color:</p>
//             <InputColor initialValue="#ffffff" placement="right" />
//             </div>
//           </div>
//         </div>
//         <Divider />
//         <button
