import { Drawer } from "rsuite";
import { useForm } from "react-hook-form";
import { formStyles } from "./bottomNavConsts";
import { connect } from "react-redux";
import { postNewProject } from "../../actions";

const NewProject = ({ show, toggleDrawer, postNewProject, user_data }) => {
  const newProjectStyles = {
    width: "30%",
    marginBottom: "3.7rem",
  };

  const { register, handleSubmit } = useForm();
  function onSubmitHandler(data) {
    postNewProject(user_data.username, user_data.user_id, data.new_project_name)
    toggleDrawer();
  }

  return (
    <>
      <Drawer
        show={show}
        onHide={toggleDrawer}
        placement="bottom"
        style={newProjectStyles}
      >
        <Drawer.Header>New Project</Drawer.Header>
        <Drawer.Body>
          <form onSubmit={handleSubmit(onSubmitHandler)} style={formStyles}>
            <label>Create New Project</label>
            <input
              type="text"
              name="new_project_name"
              placeholder="enter name"
              ref={register}
            />
            <button className="primaryButton">Commit</button>
          </form>
        </Drawer.Body>
      </Drawer>
    </>
  );
};

const mapStateToProps = (state) => {
  return{
    user_data: state.user_data
  }
}

export default connect(mapStateToProps,{postNewProject})(NewProject);
