import React, { useState } from "react";
import { Nav, Navbar, Icon } from "rsuite";
import NewCode from "./bottomNavContents/newCode";
import NewProject from "./bottomNavContents/newProject";
import SettingsMenu from "./bottomNavContents/settingsMenu";

const BottomNav = () => {
  const navBarStyles = {
    position: "absolute",
    bottom: 0,
    width: "100%",
  };

  const headerStyle = {
    display: "inline-block",
    marginTop: "4%",
    marginLeft: "0.3rem",
  };

  const [show, setShow] = useState(false);
  const [showPr, setShowPr] = useState(false);
  const [showCd, setShowCd] = useState(false);

  const toggleDrawer = () => {
    if (show === false) {
      setShow(true);
    } else {
      setShow(false);
    }
  };

  const toggleProjectButton = () => {
    if (showPr === false) {
      setShowPr(true)
    }else{
      setShowPr(false)
    }
  }

  const toggleCodeButton = () => {
    if (showCd === false){
      setShowCd(true)
    }else{
      setShowCd(false)
    }
  }

  return (
    <>
      <SettingsMenu show={show} toggleDrawer={toggleDrawer}/>
      <NewProject show={showPr} toggleDrawer={toggleProjectButton}/>
      <NewCode show={showCd} toggleDrawer={toggleCodeButton} />

      <Navbar className={"unstyledList"} style={navBarStyles}>
        <Navbar.Header>
          <h2 style={headerStyle}>Tools:</h2>
        </Navbar.Header>
        <Navbar.Body>
          <Nav>
            <Nav.Item onClick={()=>{
              toggleProjectButton()
            }}>+ Project</Nav.Item>
            <Nav.Item>+ Code</Nav.Item>
          </Nav>
          <Nav pullRight>
            <Nav.Item
              icon={<Icon icon="cog" />}
              onClick={() => {
                toggleDrawer();
              }}
            >
              Settings
            </Nav.Item>
          </Nav>
        </Navbar.Body>
      </Navbar>
    </>
  );
};

export default BottomNav;
