import React, { useEffect, useState } from "react";
import { Divider } from "rsuite";
import { connect } from "react-redux";
import {
  fetchCodeMarkers,
  fetchCodeSnippets,
} from "../../../../actions/fetchActions";
import Snippet from "./snippet";

const SnippetPane = ({
  snippets,
  fetchCodeSnippets,
  fetchCodeMarkers,
  user_data,
  currentPanel,
  cIndex,
  codeId,
  section
}) => {
  const snippetContainerSytles = {
    width: "35%",
  };

  const snippetHeaderStyle = {
    margin: ".5rem auto",
  };

  const paneDescriptionStyle = {
    width: "90%",
    margin: "0 auto",
  };

  useEffect(() => {
    fetchCodeSnippets(user_data.username, user_data.user_id, currentPanel , codeId);
    fetchCodeMarkers( user_data.username, user_data.user_id, currentPanel, codeId)
  }, [cIndex]);

  return (
    <div style={snippetContainerSytles}>
      <h1 style={snippetHeaderStyle}>{section[codeId].code_title}</h1>
      <div>
        <p style={paneDescriptionStyle}>
          {code_title[codeId].description}
        </p>
        <Divider />
      </div>
      <Snippet snips={snippets}/>
    </div>
  );
};

const mapStateToProps = (state) => {
  return {
    snippets: state.projects_data.snippets,
    user_data: state.user_data,
    currentPanel: state.application_state.currentPanel,
    cIndex: state.application_state.currentCode,
    codeId: state.application_state.currentCodeID,
    section: state.projects_data.sections
  };
};

export default connect(mapStateToProps, {
  fetchCodeSnippets,
  fetchCodeMarkers
})(SnippetPane);
