import React, { useState, useEffect } from "react";
import { Nav } from "rsuite";
import { connect } from "react-redux";
import {
  fetchCodeSection,
  fetchCodeSnippets,
  fetchCodeMarkers,
} from "../../../actions/index";

const CodeList = ({
  sections,
  current_panel,
  user_data,
  setCurrentCode,
  fetchCodeSnippets,
  fetchCodeMarkers,
  setCurrentSnippets,
  snippets,
  ...props
}) => {
  const [active, setActive] = useState(1);

  const findSectionIndex = (item) => {
    let index = sections.indexOf(item);
    setCurrentCode(index);
  };

  const fetchCalls = (active) => {
    fetchCodeMarkers(user_data.name, user_data.user_id, current_panel, active);
    fetchCodeSnippets(user_data.name, user_data.user_id, current_panel, active);
  };

  function handleSelect(active) {
    fetchCalls(active);
    setCurrentSnippets(snippets);
    setActive(active);
  }

  return (
    <div>
      <Nav
        {...props}
        vertical
        activeKey={active}
        onSelect={handleSelect}
        appearance="subtle"
      >
        <Nav.Item> </Nav.Item>
        {sections.map((section) => (
          <Nav.Item
            onClick={() => {
              findSectionIndex(section);
            }}
            eventKey={section.code_id}
          >
            {section.code_title}
          </Nav.Item>
        ))}
      </Nav>
    </div>
  );
};

const mapStateToProps = (state) => {
  return {
    sections: state.projects_data.sections,
    current_panel: state.application_state.currentPanel,
    user_data: state.user_data,
    snippets: state.application_state.snippets,
  };
};

export default connect(mapStateToProps, {
  fetchCodeSection,
  fetchCodeSnippets,
  fetchCodeMarkers,
})(CodeList);
