import React, { useState } from "react";
import { Nav, Dropdown, Modal } from "rsuite";

const EditorMenu = ({ snippets, setSnippets, selected, editorMode, theme }) => {
  const themes = [
    "monokai",
    "github",
    "tomorrow",
    "kuroir",
    "twilight",
    "xcode",
    "textmate",
    "solarized dark",
    "solarized light",
    "terminal",
  ];

  const modes = [
    "javascript",
    "java",
    "python",
    "xml",
    "ruby",
    "sass",
    "markdown",
    "mysql",
    "json",
    "html",
    "handlebars",
    "golang",
    "csharp",
    "coffee",
    "css",
  ];

  const [newSnippetMenu, setNewSnippetMenu] = useState({
    backdrop: false,
    show: false,
  });

  const open = () => {
    setNewSnippetMenu({
      backdrop: true,
      show: true,
    });
  };

  const close = () => {
    setNewSnippetMenu({
      backdrop: false,
      show: false,
    });
  };

  const NewSnippetForm = () => {
    return (
      <div>
        <button
          className="primaryButton"
          style={{ width: "6rem", marginRight: "0.5rem" }}
        >
          Set Snippet
        </button>
        <button className="secondaryButton">Cancel</button>
      </div>
    );
  };

  function snippetHandler(location) {
    open();
    setSnippets([
      ...snippets,
      {
        startRow: location[0].row,
        startCol: location[0].column,
        endRow: location[1].row,
        endCol: location[1].column,
        className: "markerTest2",
        snippet_id: 1,
      },
    ]);
  }

  return (
    <>
      <Modal backdrop={newSnippetMenu.backdrop} show={newSnippetMenu.show}>
        <Modal.Title>Create New Snippet</Modal.Title>
        <Modal.body></Modal.body>
        <Modal.Footer>
          <NewSnippetForm />
        </Modal.Footer>
      </Modal>
      <div
        style={{
          display: "flex",
          flexFlow: "row",
          justifyContent: "space-between",
          marginTop: "0.5rem",
          marginBottom: "0.5rem",
        }}
      >
        <Nav>
          <Nav.Item
            onClick={() => {
              snippetHandler(selected);
            }}
          >
            + Set Snippet
          </Nav.Item>
        </Nav>
        <Nav>
          <Dropdown title={theme}>
            {themes.map((theme) => (
              <Dropdown.Item>{theme}</Dropdown.Item>
            ))}
          </Dropdown>
          <Dropdown title={editorMode}>
            {modes.map((mode) => (
              <Dropdown.Item>{mode}</Dropdown.Item>
            ))}
          </Dropdown>
        </Nav>
      </div>
    </>
  );
};

export default EditorMenu;
