import React, { useState } from "react";
import CodeList from "./codeList";
import Editor from "./editorPane/editor";
import { Divider } from "rsuite";
import SnippetPane from "./snippetPane/snippetPane";
import { connect } from "react-redux";

function ProjectView({sections}) {
  const codeListStyles = {
    width: "10rem",
    height: "95vh",
  };

  const [currentCode, setCurrentCode] = useState("Empty");
  const [currentSnippets, setCurrentSnippets] = useState([]);

  return (
    <div
      style={{
        display: "flex",
      }}
    >
      <CodeList 
        style={codeListStyles} 
        setCurrentCode={setCurrentCode}
        setCurrentSnippets={setCurrentSnippets}/>
      <Editor currentCode={currentCode}/>
      <Divider style={{ height: "95vh" }} vertical />
      {/* <SnippetPane 
        setCurrentSnippets={setCurrentSnippets}
        currentSnippets={currentSnippets}/> */}
    </div>
  );
}

const mapStateToProps = (state) =>{
    return{
        sections: state.projects_data.sections,
    }
}

export default connect(mapStateToProps,{}) (ProjectView);
