import EditorMenu from "./editorMenu";

const Editor = () => {
    const editorStyle = {
        width: "30rem"
    }
    return(
        <div style={{marginLeft:"0.5rem"}}>
            <EditorMenu />
            <AceEditor  style={editorStyle}/>
        </div>
    )
}

export default Editor
