import React, { useState } from "react";
import CodeList from "./codeList";
import Editor from "./editor";
import { Divider } from "rsuite";
import SnippetPane from "./snippetPane";
import { connect } from "react-redux";

function ProjectView({sections}) {
  const codeListStyles = {
    width: "10rem",
    height: "95vh",
  };

  const [currentCode, setCurrentCode] = useState(1)

  return (
    <div
      style={{
        display: "flex",
      }}
    >
      <CodeList style={codeListStyles} setCurrentCode={setCurrentCode}/>
      <Editor currentCode={currentCode}/>
      <Divider style={{ height: "95vh" }} vertical />
      <SnippetPane />
    </div>
  );
}

const mapStateToProps = (state) =>{
    return{
        sections: state.projects_data.sections,
    }
}

export default connect(mapStateToProps,{}) (ProjectView);
