import React from "react";
import AceEditor from "react-ace";
import "ace-builds/src-noconflict/mode-javascript";
import "ace-builds/src-noconflict/theme-ambiance";
import EditorMenu from "./editorMenu";

const Editor = () => {
    const editorStyle = {
        width: "35rem",
        height: "85vh"
    }

    function onChange(newValues) {
        console.log("Editor Change: ",newValues)
    }

    function onSelection(selection) {
        // console.log(selection)
        console.log(selection.anchor)
        console.log(selection.lead)
    }

    const annoTest = [{
        row: 0,
        column: 3,
        text: "here is an annotation"
    }]

    const markerTest = [
        {
         startRow: 0,
         startCol:2,
         endRow:3,
         endCol: 20,
         className:"markerTest",
        },
        {
            startRow:4,
            startCol:2,
            endRow:5,
            endCol: 20,
            className:"markerTest2"
        }
    ]

    return(
        <div style={{marginLeft:"0.5rem"}}>
            <EditorMenu />
            <AceEditor
                markers={markerTest}
                annotations={annoTest}
                onSelectionChange={onSelection}
                wrapEnabled={true}
                onChange={onChange} 
                style={editorStyle}
                mode="javascript"
            />
        </div>
    )
}

export default Editor
