import React, { useState } from "react";
import LoginForm from "./loginForm";
import { Whisper} from "rsuite";
import SignUp from "./signUp";
import { connect }  from "react-redux";

function Login(){
    const [loginDetails, setLoginDetails] = useState()

    const loginBounds = {
        margin: "15rem auto",
        width: "15rem",
    }

    const button_styles = {
        width: "5rem",
        marginBottom: "0.5rem"
    }

    return(
        <div style={loginBounds}>
            <LoginForm setLoginDetails={setLoginDetails} button_styles={button_styles}/>
            <Whisper
                trigger="click"
                speaker={(props, ref)=>{
                    const {className, left, top} = props;
                    return <SignUp style={{left, top}} className={className} setLoginDetails={setLoginDetails} ref={ref}/>
                }}>
                <button
                    className="secondaryButton"
                >
                    Sign-Up
                </button>
            </Whisper>

            <br/>
            <br/>
            <button onClick={(e) =>{
                e.preventDefault()
                console.log(loginDetails)
            }}>Console Log</button>
        </div>
    )
}

const mapStateToProps = (state) => {
    user_data: state.user_data
}

export default connect(mapStateToProps)(Login)
